Select  @{pipeline().parameters.ColumnList}  
FROM @{pipeline().parameters.SourceTableName}
where  LastModifiedDate >= {ts'@{variables('brpExtractFrom')}'} AND
LastModifiedDate <= {ts'@{variables('brpExtractTo')}'}

SELECT MAX(LastModifiedDate) AS HighWaterMark
FROM @{pipeline().parameters.SourceTableName}
WHERE LastModifiedDate >= {ts'@{variables('brpExtractFrom')}'}
