Select   @{pipeline().parameters.ColumnList}  
FROM @{pipeline().parameters.SourceTableName}
where  LastModifiedDate >= @{variables('brpExtractFrom')} AND
LastModifiedDate <= @{variables('brpExtractTo')}

SELECT LastModifiedDate
FROM @{pipeline().parameters.SourceTableName}
where LastModifiedDate >= @{variables('brpExtractFrom')}
order by LastModifiedDate desc 
limit 1
